Enum.each siemens_phones, fn n ->
  hash = n
  parsed_name = Regex.run(~r/(.+?)\s(\S+)(\s(.*))?/, hash[:name])

  brand_name = parsed_name |> Enum.at(1)
  model_name = parsed_name |> Enum.at(2)
  edition_name = parsed_name |> Enum.at(3)

  if edition_name == nil do
    edition_name = "Default"
  else
    edition_name = edition_name |> String.slice(1..-1)
  end

  brand = Repo.get_by(Brand, name: brand_name)
  if brand == nil do
    {:ok, brand} = %Brand{name: brand_name} |> Brand.changeset(%{}) |> Repo.insert_or_update
  end

  phone = Repo.get_by(Phone, name: model_name)
  if phone == nil do
    {:ok, phone} = %Phone{brand_id: brand.id, name: model_name, network: hash[:network], features: hash[:features], weight: hash[:weight], size: hash[:size], battery: hash[:battery]}
                  |> Phone.changeset(%{})
                  |> Repo.insert_or_update
  end

  {:ok, edition} = %PhoneEdition{phone_id: phone.id, name: edition_name, limited: hash[:limited], notes: hash[:notes], release: hash[:release]}
                  |> PhoneEdition.changeset(%{})
                  |> Repo.insert

  Enum.each hash[:photos], fn photo ->
    %Picture{phone_edition_id: edition.id, url: photo}
    |> Picture.changeset(%{})
    |> Repo.insert!
  end

end